import React, { useState, useEffect } from 'react';

const Home = ({ LeftPage, ThreeModel }) => {
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 767);

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth <= 767);
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  return (
    <>
      {/* Three.js model as a full-page background */}
      <div className="absolute inset-0 z-0">
        <ThreeModel />
      </div>

      {/* Foreground content */}
      <div className={`relative flex h-screen ${isMobile ? 'flex-col' : ''} z-1 h-50`}>
      <div
          className={`w-1/4 bg-zinc-900 shadow-lg rounded-lg p-4 overflow-y-auto m-2 ${
            isMobile ? 'w-full' : 'ml-80'
          }`}
        >
          <LeftPage className = "h-50"/>
        </div>
      </div>
    </>
  );
};

export default Home;
