import React, { useState, useEffect } from 'react';

const Home = ({ LeftPage, ThreeModel }) => {
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 767);

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth <= 767);
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  return (
    <>
      {/* Three.js model as full-screen background */}
      <div className="fixed inset-0 z-0">
        <ThreeModel />
      </div>

      {/* Main content with z-index higher than the Three.js model */}
      <div className={`relative flex h-screen ${isMobile ? 'flex-col h-screen' : ''} z-10`}>
        <div className={`w-1/4 bg-zinc-900 shadow-lg rounded-lg p-4 overflow-y-auto m-2 ${isMobile ? 'w-full' : ''}`}>
          <LeftPage />
        </div>
      </div>
    </>
  );
};
export default Home;
