import { useThree } from '@react-three/fiber';
import { useEffect } from 'react';

const CameraSetup = () => {
  const { camera } = useThree();

  useEffect(() => {
    camera.fov = 50;
    camera.near = .1;
    camera.far = 1000;
    camera.position.set(0, 5, 15);
    camera.updateProjectionMatrix();
  }, [camera]);

  return null;
};

export default CameraSetup;
