import React, { useState, useEffect } from 'react';

const TwoPageLayout = ({LeftPage, threeModel}) => {
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 767);

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth <= 767);
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);
  return (
    <>
     <threeModel className = "z-0"/>
    <div className={`flex h-screen ${isMobile ? 'flex-col h-screen' : ''} z-10`}>
      {/* Left page takes up 1/4 of the width */}


      {/* Right page takes up 3/4 of the width */}
      <div className={`w-3/4 p-4 overflow-y-auto m-2 ${isMobile ? 'w-full' : ''}`}>

      </div>
    </div>
    </>


  );

};

export default TwoPageLayout;
