import React, { useRef, useMemo } from 'react';
import { useFrame } from '@react-three/fiber';
import * as THREE from 'three';

const Spheres = () => {
  const numColumns = 100; // Fewer columns for performance
  const numRows = 100; // Fewer rows for performance
  const sphereRadius = 0.07; // Slightly larger spheres for visibility
  const meshRef = useRef();

  // Memoize positions for instanced mesh
  const positions = useMemo(() => {
    const posArray = [];
    for (let i = 0; i < numColumns * numRows; i++) {
      const x = (i % numColumns) * 0.5 - (numColumns / 2) * 0.5;
      const y = 0;
      const z = Math.floor(i / numColumns) * 0.5 - (numRows / 2) * 0.5;
      posArray.push([x, y, z]);
    }
    return posArray;
  }, [numColumns, numRows]);

  // Animation loop
  useFrame(({ clock, camera }) => {
    const time = clock.getElapsedTime();
    const waveSpeed = 0.5; // Increased speed for more intense waves
    const waveFrequency = 0.1; // Higher frequency for sharper waves

    for (let i = 0; i < numColumns * numRows; i++) {
      const id = i;
      const dummy = new THREE.Object3D();
      const [x, y, z] = positions[i];

      // More intense wave effect
      const waveOffset = z * waveFrequency + x * waveFrequency;
      const waveHeight = Math.sin(time * waveSpeed + waveOffset) * 0.5;

      dummy.position.set(x, waveHeight, z);

      // Calculate distance from camera for fading effect
      const distance = dummy.position.distanceTo(camera.position);
      const fadeFactor = Math.max(0, 1 - distance / 30); // Adjust fade range

      dummy.scale.set(fadeFactor, fadeFactor, fadeFactor);
      dummy.updateMatrix();
      meshRef.current.setMatrixAt(id, dummy.matrix);

      // Update color and transparency
      const color = new THREE.Color(`hsl(0, 0%, ${fadeFactor * 70}%)`);
      meshRef.current.setColorAt(id, color);
    }
    meshRef.current.instanceMatrix.needsUpdate = true;
    meshRef.current.instanceColor.needsUpdate = true;
  });

  return (
    <instancedMesh ref={meshRef} args={[null, null, numColumns * numRows]}>
      <sphereGeometry args={[sphereRadius, 8, 8]} />
      <meshStandardMaterial color="#808080" transparent />
    </instancedMesh>
  );
};

export default Spheres;
