import React, { useRef, useMemo } from 'react';
import { useFrame } from '@react-three/fiber';
import * as THREE from 'three';

const Spheres = () => {
  const numColumns = 200; // Increase columns for a wider grid
  const numRows = 200; // Reduce rows to lessen load
  const sphereRadius = 0.05; // Smaller spheres
  const meshRef = useRef();

  // Memoize positions for instanced mesh
  const positions = useMemo(() => {
    const posArray = [];
    for (let i = 0; i < numColumns * numRows; i++) {
      const x = (i % numColumns) * 0.3 - (numColumns / 2) * 0.3;
      const y = 0;
      const z = Math.floor(i / numColumns) * 0.3 - (numRows / 2) * 0.3;
      posArray.push([x, y, z]);
    }
    return posArray;
  }, [numColumns, numRows]);

  // Animation loop
  useFrame(({ clock }) => {
    const time = clock.getElapsedTime();
    const waveSpeed = 0.2;
    const waveFrequency = 0.05;

    for (let i = 0; i < numColumns * numRows; i++) {
      const id = i;
      const dummy = new THREE.Object3D();
      const [x, y, z] = positions[i];

      // Smooth wave effect with less aggressive movement
      const waveOffset = z * waveFrequency;
      const waveHeight = Math.sin(time * waveSpeed + waveOffset) * 0.2;

      dummy.position.set(x, waveHeight, z);
      dummy.updateMatrix();
      meshRef.current.setMatrixAt(id, dummy.matrix);
    }
    meshRef.current.instanceMatrix.needsUpdate = true;
  });

  return (
    <instancedMesh ref={meshRef} args={[null, null, numColumns * numRows]}>
      <sphereGeometry args={[sphereRadius, 8, 8]} />
      <meshStandardMaterial color="#00bfff" />
    </instancedMesh>
  );
};

export default Spheres;
