import React, { useRef, useState } from 'react'
import { Canvas, useFrame } from '@react-three/fiber'

const Box = ({position}) => {
    const myMesh = React.useRef()
    useFrame(({clock}) => {
      myMesh.current.rotation.x = clock.getElapsedTime()
      //.position.y
      //Math.sin(clock.getElapsedTime())
    })

    return (
      <>
        <mesh ref={myMesh} position={position}>
          <boxGeometry args={[.5, .5, .5]} />
          <meshPhongMaterial />
        </mesh>
      </>
    )

  }
  const Boxes = () => {
    const numBoxes = 25;
    const boxesPerRow = 5;
    const boxPositions = useMemo(() => {
      return Array.from({ length: numBoxes }, (_, i) => [
        (i % boxesPerRow) * 1.5 - (boxesPerRow / 2),
        Math.floor(i / boxesPerRow) * 1.5 - (numBoxes / boxesPerRow / 2),
        0,
      ]);
    }, [numBoxes, boxesPerRow])
    return (
      <>
        {boxPositions.map((position, index) => {
          const rowIndex = Math.floor(index / boxesPerRow);
          const color = rowIndex === greenRow ? 'green' : 'hotpink';
          return <Box key={index} position={position} color={color} />;
        })}
      </>
    );



  }
  export default Boxes;
