import React, { useRef, useMemo } from 'react';
import { useFrame } from '@react-three/fiber';
import * as THREE from 'three';

const Spheres = () => {
  const numColumns = 80; // Fewer instances for better performance
  const numRows = 80;
  const sphereRadius = 0.1; // Larger spheres for visibility
  const meshRef = useRef();

  // Memoized positions
  const positions = useMemo(() => {
    const posArray = [];
    for (let i = 0; i < numColumns * numRows; i++) {
      const x = (i % numColumns) * 0.6 - (numColumns / 2) * 0.6;
      const y = 0;
      const z = Math.floor(i / numColumns) * 0.6 - (numRows / 2) * 0.6;
      posArray.push([x, y, z]);
    }
    return posArray;
  }, [numColumns, numRows]);

  // Initialize positions and matrices
  React.useEffect(() => {
    const dummy = new THREE.Object3D();
    const color = new THREE.Color();
    for (let i = 0; i < numColumns * numRows; i++) {
      const [x, y, z] = positions[i];
      dummy.position.set(x, y, z);
      dummy.updateMatrix();
      meshRef.current.setMatrixAt(i, dummy.matrix);
      color.set('#a9a9a9'); // Light gray color
      meshRef.current.setColorAt(i, color);
    }
    meshRef.current.instanceMatrix.needsUpdate = true;
    meshRef.current.instanceColor.needsUpdate = true;
  }, [positions]);

  // Animation loop
  useFrame(({ clock, camera }) => {
    const time = clock.getElapsedTime();
    const waveSpeed = 1; // Increased wave speed
    const waveFrequency = 0.3; // Increased frequency for sharper waves
    const dummy = new THREE.Object3D();
    const color = new THREE.Color();

    for (let i = 0; i < numColumns * numRows; i++) {
      const [x, y, z] = positions[i];
      const waveOffset = z * waveFrequency + x * waveFrequency;
      const waveHeight = Math.sin(time * waveSpeed + waveOffset) * 1; // More intense wave height

      dummy.position.set(x, waveHeight, z);

      // Fade effect based on distance to camera
      const distance = dummy.position.distanceTo(camera.position);
      const fadeFactor = Math.max(0, 1 - distance / 30);
      dummy.scale.set(fadeFactor, fadeFactor, fadeFactor);
      dummy.updateMatrix();
      meshRef.current.setMatrixAt(i, dummy.matrix);

      // Dynamic color adjustment
      color.setHSL(0, 0, fadeFactor * 0.8 + 0.2); // Slightly lighter gray based on fade
      meshRef.current.setColorAt(i, color);
    }

    meshRef.current.instanceMatrix.needsUpdate = true;
    meshRef.current.instanceColor.needsUpdate = true;
  });

  return (
    <instancedMesh ref={meshRef} args={[null, null, numColumns * numRows]}>
      <sphereGeometry args={[sphereRadius, 16, 16]} />
      <meshStandardMaterial vertexColors />
    </instancedMesh>
  );
};

export default Spheres;
